INSERT INTO `wp_posts` VALUES (177,1,'2020-10-19 21:51:07','2020-10-19 21:51:07','<!-- wp:heading {\"align\":\"left\",\"level\":1,\"textColor\":\"black\"} -->\n<h1 class=\"has-text-align-left has-black-color has-text-color\"><strong>Choice Powers Change offers an array of tailored trainings including:</strong></h1>\n<!-- /wp:heading -->\n\n<!-- wp:columns -->\n<div class=\"wp-block-columns\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:image {\"id\":176,\"sizeSlug\":\"large\",\"className\":\"is-style-default\"} -->\n<figure class=\"wp-block-image size-large is-style-default\"><img src=\"https://choice-powers-change-consulting.local/wp-content/uploads/2020/10/Love-1024x768.jpg\" alt=\"\" class=\"wp-image-176\"/></figure>\n<!-- /wp:image --></div>\n<!-- /wp:column -->\n\n<!-- wp:column {\"verticalAlignment\":\"center\",\"width\":47.5} -->\n<div class=\"wp-block-column is-vertically-aligned-center\" style=\"flex-basis:47.5%\"><!-- wp:paragraph {\"textColor\":\"black\"} -->\n<p class=\"has-black-color has-text-color\"><strong>Community Mapping:</strong> This training integrates community-based<br>exploration and individual interest into person-centered Outcomes and<br>provides an overview of how to embrace HCBS/Final rule. It is designed to<br>inspire excitement regarding how to support people to have meaningful<br>relationships with their community.</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:paragraph {\"textColor\":\"black\"} -->\n<p class=\"has-black-color has-text-color\"><strong>The Learning Community 2-day Person Centered Thinking Skills<br>Training:</strong> Marlene and Rachael are certified 2-day trainers.<br>This 2-day training results in people inspired to create choice, change and<br>optimism for themselves and others.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"textColor\":\"black\"} -->\n<h2 class=\"has-black-color has-text-color\"><strong>How to Contact Us</strong></h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"textColor\":\"black\"} -->\n<p class=\"has-black-color has-text-color\">Marlene McCollum and Rachael Newkirk</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"black\"} -->\n<p class=\"has-black-color has-text-color\">Choice Powers Change Consulting<br>PO Box 3105<br>Chico, CA 95927<br><a href=\"tel:530-520-7521\">(530) 520-7521</a><br><a href=\"mailto:choicepowerschange@gmail.com\">choicepowerschange@gmail.com</a></p>\n<!-- /wp:paragraph -->','Services','','inherit','closed','closed','','15-revision-v1','','','2020-10-19 21:51:07','2020-10-19 21:51:07','',15,'https://choice-powers-change-consulting.local/15-revision-v1/',0,'revision','',0);
