INSERT INTO `wp_wc_admin_notes` VALUES (1,'wc-admin-wc-helper-connection','info','en_US','Connect to WooCommerce.com','Connect to get important product notifications and updates.','{}','unactioned','woocommerce-admin','2020-10-15 01:42:06',NULL,0,'plain','',0,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (2,'wc-admin-onboarding-profiler-reminder','update','en_US','Welcome to WooCommerce! Set up your store and start selling','We\'re here to help you going through the most important steps to get your store up and running.','{}','actioned','woocommerce-admin','2020-10-15 01:42:07',NULL,0,'plain','',0,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (3,'wc-admin-onboarding-email-marketing','info','en_US','Tips, product updates, and inspiration','We\'re here for you - get tips, product updates and inspiration straight to your email box','{}','unactioned','woocommerce-admin','2020-10-15 01:42:16',NULL,0,'plain','',0,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (4,'wc-admin-marketing-intro','info','en_US','Connect with your audience','Grow your customer base and increase your sales with marketing tools built for WooCommerce.','{}','unactioned','woocommerce-admin','2020-10-15 01:42:17',NULL,0,'plain','',0,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (5,'ecomm-need-help-setting-up-your-store','info','en_US','Need help setting up your Store?','Schedule a free 30-min <a href=\"https://wordpress.com/support/concierge-support/\">quick start session</a> and get help from our specialists. Were happy to walk through setup steps, show you around the WordPress.com dashboard, troubleshoot any issues you may have, and help you the find the features you need to accomplish your goals for your site.','{}','pending','woocommerce.com','2020-10-15 01:42:18',NULL,0,'plain','',0,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (6,'woocommerce-services','info','en_US','WooCommerce Shipping & Tax','WooCommerce Shipping &amp; Tax helps get your store ready to sell as quickly as possible. You create your products. We take care of tax calculation, payment processing, and shipping label printing! Learn more about the extension that you just installed.','{}','pending','woocommerce.com','2020-10-15 01:42:18',NULL,0,'plain','',0,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (7,'ecomm-unique-shopping-experience','info','en_US','For a shopping experience as unique as your customers','Product Add-Ons allow your customers to personalize products while theyre shopping on your online store. No more follow-up email requestscustomers get what they want, before theyre done checking out. Learn more about this extension that comes included in your plan.','{}','pending','woocommerce.com','2020-10-15 01:42:18',NULL,0,'plain','',0,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (8,'wc-admin-mobile-app','info','en_US','Install Woo mobile app','Install the WooCommerce mobile app to manage orders, receive sales notifications, and view key metrics  wherever you are.','{}','unactioned','woocommerce-admin','2020-10-17 06:50:47',NULL,0,'plain','',0,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (9,'wc-admin-selling-online-courses','marketing','en_US','Do you want to sell online courses?','Online courses are a great solution for any business that can teach a new skill. Since courses dont require physical product development or shipping, theyre affordable, fast to create, and can generate passive income for years to come. In this article, we provide you more information about selling courses using WooCommerce.','{}','unactioned','woocommerce-admin','2020-10-18 22:39:56',NULL,0,'plain','',0,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (10,'mailchimp-for-woocommerce-incomplete-install','warning','en_US','Mailchimp For WooCommerce','Plugin is not yet connected to a Mailchimp account. To complete the connection, open the settings page.','{\"getting_started\":true,\"activated\":1603060980,\"activated_formatted\":\"October 18th\"}','unactioned','mailchimp-for-woocommerce','2020-10-18 22:43:00',NULL,0,'plain','',0,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (11,'wc-admin-onboarding-payments-reminder','info','en_US','Start accepting payments on your store!','Take payments with the provider thats right for you - choose from 100+ payment gateways for WooCommerce.','{}','unactioned','woocommerce-admin','2020-10-21 00:03:35',NULL,0,'plain','',0,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (12,'wc-admin-woocommerce-payments','marketing','en_US','Try the new way to get paid','Securely accept credit and debit cards on your site. Manage transactions without leaving your WordPress dashboard. Only with WooCommerce Payments.','{}','unactioned','woocommerce-admin','2020-10-23 03:41:38',NULL,0,'plain','',0,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (13,'wc-admin-store-notice-giving-feedback-2','info','en_US','Give feedback','Now that youve chosen us as a partner, our goal is to make sure we\'re providing the right tools to meet your needs. We\'re looking forward to having your feedback on the store setup experience so we can improve it in the future.','{}','unactioned','woocommerce-admin','2020-10-23 03:41:38',NULL,0,'plain','',0,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (14,'wc-admin-insight-first-sale','survey','en_US','Did you know?','A WooCommerce powered store needs on average 31 days to get the first sale. You\'re on the right track! Do you find this type of insight useful?','{}','unactioned','woocommerce-admin','2020-10-23 03:41:39',NULL,0,'plain','',0,'info');
